import { Fragment } from "react"
import { Dialog, Transition } from "@headlessui/react"
import {
	AcademicCapIcon,
	XMarkIcon,
	CheckCircleIcon,
	ChevronDownIcon,
	ChevronUpIcon,
} from "@heroicons/react/24/outline"
import { CheckCircleIcon as CheckCircleIconSolid } from "@heroicons/react/24/solid"
import Request from "../../Request"
import { useState, useEffect } from "react"

const BrainstormInputModal = ({
	open,
	setOpen,
	brainstormThreads,
	setBrainstormThreads,
	selectedBrainstormThread,
	setSelectedBrainstormThread,
	socket,
}) => {
	useEffect(() => {
		if (!open) {
			setInputText("")
		}
		setTimeout(() => {
			setSelectedQuestion(null)
		}, 500)
	}, [open])

	const [inputText, setInputText] = useState("")
	const [selectedQuestion, setSelectedQuestion] = useState(null)
	const questionsList = [
		{
			id: 1,
			question: "What was your most traumatic memory from high school?",
			examples: [
				"I was held at gunpoint outside of my school in 9th grade. My friends and I had just left the basketball game.",
				"I was viciously bullied by a group of classmates who would physically intimidate me in the hallways and spread cruel rumors about me. They knew I was dyslexic.",
				"My childhood best friend died from a drug overdose suddenly during my junior year. It was the first time I had to deal with death, and I lost thirty pounds.",
			],
		},
		{
			id: 2,
			question: "What does your comfort food look like, and mean to you?",
			examples: [
				"A warm bowl of chicken soup from my late grandmother. From the day she passed away, I've eaten it to remind myself of family.",
				"A plate of spaghetti with marinara sauce and meatballs. It reminds me of the big Sunday dinners my family would have when I was growing up and brings back feelings of love, warmth, and unity.",
				"A slice of apple pie with a scoop of vanilla ice cream. It symbolizes the simpler times in my life, like summer vacations and holidays spent with my grandparents, and brings a sense of nostalgia and comfort.",
			],
		},
		{
			id: 3,
			question: "What is your spirit animal?",
			examples: [
				"I identify with the noble sloth: slow, lazy, but calculated.",
				"Flamingo. I'm flamboyant, graceful, and elegant, all at the same time.",
				"The axolotl. Like this rare salamander that can regenerate its limbs, I'm resilient and can overcome any obstacle.",
			],
		},
		// {
		// 	id: 4,
		// 	question: "What is your favorite movie?",
		// 	examples: [
		// 		"The Godfather",
		// 		"The Shawshank Redemption",
		// 		"The Dark Knight",
		// 	],
		// },
	]

	const handleSelectQuestion = (question) => {
		if (selectedQuestion === question.id) {
			setSelectedQuestion(null)
		} else {
			setSelectedQuestion(question.id)
		}
	}

	const handleNewBrainstormThread = () => {
		const threadRequest = new Request("brainstormthreads/", {
			method: "POST",
			data: {
				title: "New brainstorm!",
				description: "This is the new brainstorm's description",
			},
		})
		threadRequest
			.then((res) => {
				setBrainstormThreads([...brainstormThreads, res.data])
				setSelectedBrainstormThread(res.data.id)
				socket.send(
					JSON.stringify({
						command: inputText,
						websocket_type: "brainstorm",
						thread_id: res.data.id,
					})
				)
				setInputText("")
			})
			.catch((err) => {
				console.log(err)
			})
		setOpen(false)
	}

	const writtenEnoughClass = () => {
		return inputText.length > 50
			? " bg-delilahSecondary"
			: " bg-delilahSecondary/50"
	}

	return (
		<Transition.Root show={open} as={Fragment}>
			<Dialog as='div' className='relative z-10' onClose={setOpen}>
				<Transition.Child
					as={Fragment}
					enter='ease-out duration-300'
					enterFrom='opacity-0'
					enterTo='opacity-100'
					leave='ease-in duration-200'
					leaveFrom='opacity-100'
					leaveTo='opacity-0'
				>
					<div className='fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity' />
				</Transition.Child>

				<div className='fixed inset-0 z-10 overflow-y-auto'>
					<div className='flex min-h-full items-end justify-center p-4 text-center sm:items-center sm:p-0'>
						<Transition.Child
							as={Fragment}
							enter='ease-out duration-300'
							enterFrom='opacity-0 translate-y-4 sm:translate-y-0 sm:scale-95'
							enterTo='opacity-100 translate-y-0 sm:scale-100'
							leave='ease-in duration-200'
							leaveFrom='opacity-100 translate-y-0 sm:scale-100'
							leaveTo='opacity-0 translate-y-4 sm:translate-y-0 sm:scale-95'
						>
							<Dialog.Panel className='relative transform overflow-hidden rounded-lg bg-white px-4 pt-5 pb-4 text-left shadow-xl transition-all sm:my-8 sm:w-2/5 sm:p-6'>
								<div className='mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left'>
									<div as='h3' className='text-lg font-bold leading-6'>
										Brainstorm: Pick a Question
									</div>
									{questionsList.map((question) => {
										return (
											<div
												className={
													"mt-2 flex flex-col bg-white w-full" +
													(question.id === selectedQuestion ||
													selectedQuestion === null
														? " visible opacity-100"
														: " hidden opacity-0")
												}
												key={question.id}
											>
												<div
													className='flex flex-row justify-between hover:cursor-pointer'
													onClick={() => handleSelectQuestion(question)}
												>
													<p className='text-lg text-black'>
														{question.question}
													</p>
													{question.id === selectedQuestion ? (
														<ChevronUpIcon className='h-5 w-5 text-slate-500' />
													) : (
														<ChevronDownIcon className='h-5 w-5 text-slate-500' />
													)}
												</div>
												{question.id === selectedQuestion && (
													<>
														<div className='mt-2 flex flex-col'>
															<p className='mb-2 font-bold'>
																Here are some strong examples. Try to make yours
																similar in length, detail, and specificity.
															</p>
															{question.examples.map((example) => {
																return (
																	<div
																		className='flex flex-row mb-2'
																		key={example}
																	>
																		<p className='mr-1'>â€¢</p>
																		<p className=''>{example}</p>
																	</div>
																)
															})}
														</div>
														<div className='flex flex-col mt-4'>
															<textarea
																// make the textarea automatically become taller as a user types more
																className='w-full border-[1px] border-slate-200 rounded-lg p-2 resize-vertical h-24'
																placeholder='Start telling your story...'
																value={inputText}
																onChange={(e) => setInputText(e.target.value)}
															/>
														</div>
														<div className='flex flex-row justify-end'>
															<p>Minumum characters: {inputText.length}/50</p>
														</div>
													</>
												)}
											</div>
										)
									})}
								</div>

								<div className='mt-5 sm:mt-4 flex flex-row justify-end'>
									<button
										type='button'
										className='mt-3 inline-flex w-full justify-center rounded-md border border-gray-300 bg-white px-4 py-2 text-base font-medium text-gray-700 shadow-sm hover:text-gray-500 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 sm:mt-0 sm:w-auto sm:text-sm'
										onClick={() => setOpen(false)}
									>
										Cancel
									</button>
									<button
										type='button'
										className={
											"inline-flex w-full justify-center rounded-md border border-transparent px-4 py-2 text-base font-medium text-white shadow-sm focus:outline-none focus:ring-2 focus:ring-delilahPrimary/80 focus:ring-offset-2 sm:ml-3 sm:w-auto sm:text-sm" +
											writtenEnoughClass()
										}
										onClick={(e) => handleNewBrainstormThread(e)}
										disabled={inputText.length < 50}
									>
										Start brainstorming
									</button>
								</div>
							</Dialog.Panel>
						</Transition.Child>
					</div>
				</div>
			</Dialog>
		</Transition.Root>
	)
}

export default BrainstormInputModal
