import React, { useEffect, useState } from "react"
import Left from "./Left"
import Center from "./Center"
import Right from "./Right"
import Spinner from "./Spinner"
import Request from "./Request/index.jsx"
import FreeWrite from "./FreeWrite"
import Settings from "./Settings"
import Cookies from "universal-cookie"
import Footer from "./Footer"
import CTASubscribeModal from "./CTASubscribeModal"

const cookies = new Cookies()

const Content = () => {
	const [selectedMode, setSelectedMode] = useState("chat")

	// chat mode
	const [threads, setThreads] = useState([])
	const [selectedThread, setSelectedThread] = useState("")
	const [snippets, setSnippets] = useState([])

	// freewrite mode
	const [freewriteThreads, setFreewriteThreads] = useState([])
	const [selectedFreewriteThread, setSelectedFreewriteThread] = useState("")

	// user info
	const [userID, setUserID] = useState("")
	const [subscription, setSubscription] = useState({})

	// close navbar
	const [showNav, setShowNav] = useState(true)

	// settings modal
	const [showSettings, setShowSettings] = useState(false)

	// websocket connection
	const [socket, setSocket] = useState(null)

	// show CTA subscribed modal
	const [showCTASubscribed, setShowCTASubscribed] = useState(false)

	useEffect(() => {
		setTimeout(() => {
			initData()
		}, 1000)
	}, [])

	useEffect(() => {
		const getCookie = new Promise((resolve, _) => {
			const obtainCookie = () => {
				const tokenCookie = cookies.get("token")
				console.log("Trying to get cookie...")
				if (tokenCookie !== undefined) {
					console.log("Got cookie! ", tokenCookie)
					return tokenCookie
				}
			}
			const tokenCookie = obtainCookie()
			if (tokenCookie !== undefined) {
				resolve(tokenCookie)
			} else {
				setTimeout(() => {
					resolve(obtainCookie())
				}, 100)
			}
		})

		const setSocketPromise = new Promise((resolve, _) => {
			getCookie.then((val) => {
				const websocket = new WebSocket(
					process.env.REACT_APP_WS_URL + "ws/essay/?token=" + val
				)
				resolve(websocket)
			})
		})

		setSocketPromise.then((val) => {
			setSocket(val)
		})
	}, [])

	useEffect(() => {
		if (socket === null) return
		socket.onopen = (e) => {
			console.log("Connected to websocket from home")
		}

		socket.onclose = (e) => {
			console.log("Disconnected from websocket from home")
		}

		// if socket fails to connect, retry every second until it connects
		socket.onerror = (e) => {
			console.log("Error connecting to websocket from home")
			setTimeout(() => {
				setSocket(null)
				setSocket(
					new WebSocket(
						process.env.REACT_APP_WS_URL +
							"ws/essay/" +
							"?token=" +
							cookies.get("token")
					)
				)
			}, 1000)
		}

		return () => {
			socket.close()
		}
	}, [socket])

	const initData = () => {
		const meRequest = new Request("users/me/")
		meRequest
			.then((res) => {
				setUserID(res.data.user_id)
				setSubscription(res.data.subscription)
			})
			.catch((err) => {
				console.log(err)
			})

		const threadsRequest = new Request("threads/")
		threadsRequest
			.then((res) => {
				setThreads(res.data)
				setSelectedThread(res.data[0].id)
			})
			.catch((err) => {
				console.log(err)
			})

		const freewriteThreadsRequest = new Request("freewritethreads/")
		freewriteThreadsRequest
			.then((res) => {
				setFreewriteThreads(res.data)
				setSelectedFreewriteThread(res.data[0].id)
			})
			.catch((err) => {
				console.log(err)
			})

		const snippetsRequest = new Request("snippets/")
		snippetsRequest
			.then((res) => {
				setSnippets(res.data)
			})
			.catch((err) => {
				console.log(err)
			})
	}

	if (selectedThread === "") {
		return <Spinner />
	}

	return (
		<div className='flex flex-row from-[#F5F4FF] to-[#F5F4FF] bg-gradient-135'>
			<CTASubscribeModal
				open={showCTASubscribed}
				setShowCTASubscribed={setShowCTASubscribed}
				setShowSettings={setShowSettings}
			/>

			<Left
				showNav={showNav}
				threads={threads}
				setThreads={setThreads}
				selectedThread={selectedThread}
				setSelectedThread={setSelectedThread}
				selectedMode={selectedMode}
				setSelectedMode={setSelectedMode}
				freewriteThreads={freewriteThreads}
				setFreewriteThreads={setFreewriteThreads}
				selectedFreewriteThread={selectedFreewriteThread}
				setSelectedFreewriteThread={setSelectedFreewriteThread}
				setShowSettings={setShowSettings}
				setShowCTASubscribed={setShowCTASubscribed}
			/>

			{selectedMode === "chat" && showSettings === false && (
				<>
					<Center
						threads={threads}
						setThreads={setThreads}
						selectedThread={selectedThread}
						setSelectedThread={setSelectedThread}
						snippets={snippets}
						setSnippets={setSnippets}
						socket={socket}
						setShowCTASubscribed={setShowCTASubscribed}
					/>
					<Right snippets={snippets} setSnippets={setSnippets} />
				</>
			)}

			{selectedMode === "freewrite" && showSettings === false && (
				<>
					<FreeWrite
						freewriteThreads={freewriteThreads}
						setFreewriteThreads={setFreewriteThreads}
						selectedFreewriteThread={selectedFreewriteThread}
						setSelectedFreewriteThread={setSelectedFreewriteThread}
						socket={socket}
						setShowCTASubscribed={setShowCTASubscribed}
					/>
				</>
			)}

			{showSettings && <Settings subscription={subscription} userID={userID} />}
		</div>
	)
}

export default Content
