import React, { useEffect, useState } from "react"
import { useAuth0 } from "@auth0/auth0-react"
import Spinner from "./Spinner/spinner"
import Login from "./Login/"
import Content from "./Content"
import Cookies from "universal-cookie"

const Home = () => {
	const {
		isAuthenticated,
		isLoading,
		getAccessTokenSilently,
		getAccessTokenWithPopup,
	} = useAuth0()
	const cookies = new Cookies()

	const [tokenObtained, setTokenObtained] = useState(false)

	const setToken = (res) => {
		return new Promise((resolve) => {
			resolve(cookies.set("token", res, { path: "/" }))
		})
	}

	let AUDIENCE = process.env.REACT_APP_AUDIENCE

	useEffect(() => {
		if (isAuthenticated) {
			getAccessTokenSilently({
				audience: AUDIENCE,
				scope: "read:current_user",
			})
				.then((res) => {
					setToken(res).then(() => {
						setTokenObtained(true)
					})
				})
				.catch((error) => {
					getAccessTokenWithPopup({ audience: AUDIENCE })
						.then((res) => {
							setToken(res).then(() => {
								setTokenObtained(true)
							})
						})
						.catch((error) => {
							console.log("error", error)
						})
					console.log("error", error)
				})
		}
	}, [isAuthenticated])

	if (isLoading) {
		return <Spinner />
	}

	return <>{isAuthenticated && tokenObtained ? <Content /> : <Login />}</>
}

export default Home
