import "./style.css"
import { useState } from "react"
import Request from "../Request/index.jsx"
// import Modal from "@mui/material/Modal"
import TextareaAutosize from "react-textarea-autosize"

const Snippet = (props) => {
	const [open, setOpen] = useState(false)
	const handleClose = () => setOpen(false)
	const [editedSnippetTitle, setEditedSnippetTitle] = useState(
		props.snippet.title
	)
	const [editedSnippetText, setEditedSnippetText] = useState(
		props.snippet.text
	)

	const deleteSnippet = (e) => {
		const snippetsRequest = new Request(
			"snippets/" + props.snippet.id + "/",
			{
				method: "DELETE",
			}
		)
		snippetsRequest
			.then((res) => {
				const newSnippets = [...props.snippets]
				newSnippets.splice(props.index, 1)
				props.setSnippets(newSnippets)
			})
			.catch((err) => {
				console.log(err)
			})
	}

	const handleSnippetSubmit = (e) => {
		e.preventDefault()

		const snippetsRequest = new Request(
			"snippets/" + props.snippet.id + "/edit/",
			{
				method: "POST",
				data: { title: editedSnippetTitle, text: editedSnippetText },
			}
		)
		snippetsRequest
			.then((res) => {
				props.setSnippets(
					props.snippets.map((snippet) => {
						if (snippet.id === props.snippet.id) {
							snippet.title = editedSnippetTitle
							snippet.text = editedSnippetText
							return snippet
						}
						return snippet
					})
				)
				handleClose()
			})
			.catch((err) => {
				console.log(err)
			})
	}

	return (
		<div className="snippet" key={props.index}>
			<div className="snippet-header">
				{open ? (
					<input
						className="snippet-title-edit"
						id="snippet-title-edit"
						value={editedSnippetTitle}
						onChange={(e) => setEditedSnippetTitle(e.target.value)}
						autoFocus
						onFocus={function (e) {
							var val = e.target.value
							e.target.value = ""
							e.target.value = val
						}}
						placeholder="Title"
					></input>
				) : (
					<h3 className="snippet-title">
						{props.snippet.title ? props.snippet.title : "Untitled"}
					</h3>
				)}
				<div className="options-menu">
					<i
						className="fa-solid fa-pencil edit-icon"
						onClick={setOpen(true)}
					></i>
					<i
						className="fa-solid fa-trash delete-icon"
						onClick={(e) => deleteSnippet(e)}
					></i>
				</div>
			</div>
			{open ? (
				<form
					className="snippet-text-edit-container"
					onSubmit={handleSnippetSubmit}
				>
					<TextareaAutosize
						className="snippet-text-edit-area"
						id="snippet-text-edit-area"
						value={editedSnippetText}
						onChange={(e) => setEditedSnippetText(e.target.value)}
						autoFocus
						onFocus={function (e) {
							var val = e.target.value
							e.target.value = ""
							e.target.value = val
						}}
						placeholder="Body"
					></TextareaAutosize>
					<button
						className="snippet-text-edit-done-button"
						type="submit"
					>
						Done
					</button>
				</form>
			) : (
				<p className="snippet-text">{props.snippet.text}</p>
			)}
		</div>
	)
}
export default Snippet
