import { useState } from "react"
import Request from "../../Request/index.jsx"
import Menu from "@mui/material/Menu"
import MenuItem from "@mui/material/MenuItem"
import ListItemText from "@mui/material/ListItemText"
import Typography from "@mui/material/Typography"
import { useAuth0 } from "@auth0/auth0-react"
import logo from "../../../img/logo.png"

const Message = ({
	snippets,
	setSnippets,
	sendCommands,
	writtenByDelilah,
	text,
	delilahWorking,
	isLast,
}) => {
	const [contextMenu, setContextMenu] = useState(null)
	const { user } = useAuth0()
	const { picture } = user

	const handleContextMenu = (event) => {
		event.preventDefault()
		setContextMenu(
			contextMenu === null
				? {
						mouseX: event.clientX + 2,
						mouseY: event.clientY - 6,
				  }
				: // repeated contextmenu when it is already open closes it with Chrome 84 on Ubuntu
				  // Other native context menus might behave different.
				  // With this behavior we prevent contextmenu from the backdrop to re-locale existing context menus.
				  null
		)
	}

	const handleClose = () => {
		setContextMenu(null)
	}

	const handleCopy = () => {
		navigator.clipboard.writeText(window.getSelection().toString())
		handleClose()
	}

	const handleAddToSnippet = () => {
		const parameters = {
			data: { title: "", text: window.getSelection().toString() },
			method: "POST",
		}
		const snippetsRequest = new Request("snippets/", parameters)
		snippetsRequest
			.then((res) => {
				setSnippets([...snippets, res.data])
			})
			.catch((err) => {
				console.log(err)
			})
		handleClose()
	}

	const handleShowDontTell = (e) => {
		sendCommands(e, "showdonttell", window.getSelection().toString())
		handleClose()
	}

	const handleRemove = (e) => {
		sendCommands(e, "remove", window.getSelection().toString())
		handleClose()
	}

	return (
		<div
			className={
				"flex flex-row mb-4 rounded-xl pl-4 " +
				(writtenByDelilah ? "bg-white " : " ") +
				(isLast ? "mb-[250px]" : "")
			}
		>
			{writtenByDelilah && (
				<img src={picture} alt='PFP' className='h-8 w-8 rounded-2xl mt-6' />
			)}
			{!writtenByDelilah && (
				<img
					className='h-8 w-8 rounded-2xl mt-6'
					src={logo}
					alt='Delilah Logo'
				/>
			)}

			<div className='flex flex-col items-start w-full p-3 items-center justify-center'>
				<div className={`text-black w-full p-3 rounded-md`}>
					<div
						className='whitespace-pre-wrap leading-7'
						onContextMenu={handleContextMenu}
						style={{ cursor: "context-menu" }}
					>
						{text.trim()}
						<Menu
							open={contextMenu !== null}
							onClose={handleClose}
							anchorReference='anchorPosition'
							anchorPosition={
								contextMenu !== null
									? {
											top: contextMenu.mouseY,
											left: contextMenu.mouseX,
									  }
									: undefined
							}
						>
							<MenuItem onClick={handleCopy}>
								<ListItemText>Copy</ListItemText>
								<Typography variant='body2' color='text.secondary'>
									âŒ˜C
								</Typography>
							</MenuItem>
							<MenuItem
								onClick={handleAddToSnippet}
								disabled={
									!(window.getSelection().toString().length > 0) ||
									delilahWorking
								}
							>
								Add to Snippet
							</MenuItem>
							<MenuItem
								onClick={handleShowDontTell}
								disabled={
									!(window.getSelection().toString().length > 0) ||
									delilahWorking
								}
							>
								Show Don't Tell
							</MenuItem>
							<MenuItem
								onClick={handleRemove}
								disabled={
									!(window.getSelection().toString().length > 0) ||
									delilahWorking
								}
							>
								Remove
							</MenuItem>
						</Menu>
					</div>
				</div>
			</div>
		</div>
	)
}
export default Message
