import Message from "../Message"
import React, { useState, useEffect } from "react"
import Request from "../Request/index.jsx"
import CenterForm from "../CenterForm"
import EmptyThreadInfo from "./EmptyThreadInfo"

const Center = ({
	threads,
	setThreads,
	selectedThread,
	snippets,
	setSnippets,
	socket,
}) => {
	const [delilahWorking, setDelilahWorking] = useState(false)
	// Prompt Form
	const choiceTypes = ["prompt", "subject", "demographics"]
	const [formData, setFormData] = useState({
		prompt_type: "brainstorm",
		prompt_choices: [
			{
				choice_type: choiceTypes[0],
				choice: "",
				placeholder: "Enter the prompt here",
			},
			{
				choice_type: choiceTypes[1],
				choice: "",
				placeholder: "Choose an interesting topic",
			},
			{
				choice_type: choiceTypes[2],
				choice: "",
				placeholder: "Enter cool information about yourself",
			},
		],
		thread_id: selectedThread,
	})

	// Update Form / Quicktools
	const [command, setCommand] = useState("general")
	const [showTextField, setShowTextField] = useState(true)
	const [textFieldValue, setTextFieldValue] = useState("")

	useEffect(() => {
		if (socket === null) return

		socket.onmessage = (e) => {
			const data = JSON.parse(e.data)
			if (data["output_text"]) {
				setThreads(
					threads.map((thread) => {
						if (thread.id === selectedThread) {
							const lastGeneration =
								thread.generations.slice(-1)[0]
							lastGeneration.text =
								lastGeneration.text + data["output_text"]
						}
						return thread
					})
				)
			}
			if (data["prompt"]) {
				setThreads(
					threads.map((thread) => {
						if (thread.id === selectedThread) {
							return {
								...thread,
								generations: [...thread.generations, data],
							}
						}
						return thread
					})
				)
			}
			setFormData({
				...formData,
				prompt_type: "brainstorm",
				prompt_choices: [
					{ choice_type: choiceTypes[0], choice: "" },
					{ choice_type: choiceTypes[1], choice: "" },
					{ choice_type: choiceTypes[2], choice: "" },
				],
			})
			if (data["status_command"] === "DONE") {
				setTextFieldValue("")
			}

			if (data["status"] === "DONE") {
				setDelilahWorking(false)
			}
		}

		return () => {
			socket.onmessage = null
		}
	}, [threads, setThreads, selectedThread, delilahWorking])

	useEffect(() => {
		setFormData({
			...formData,
			thread_id: selectedThread,
		})
	}, [selectedThread])

	const handleChangeForm = (event, index) => {
		const { name, value } = event.target
		const newPromptChoices = [...formData.prompt_choices]

		if (event.target.name === "prompt_type")
			setFormData({
				...formData,
				[event.target.name]: event.target.value,
			})
		else newPromptChoices[index][name] = value
		setFormData({
			...formData,
			prompt_type: name === "prompt_type" ? value : formData.prompt_type,
			prompt_choices: newPromptChoices,
		})

		console.log(formData)
	}

	const sendForm = () => {
		setDelilahWorking(true)
		socket.send(
			JSON.stringify({ ...formData, websocket_type: "generation" })
		)
	}

	const handleChange = (e) => {
		setCommand(e.target.value)
		setShowTextField(e.target.value !== "")
	}

	const sendCommands = (e, valCommand, valTextFieldValue) => {
		e.preventDefault()
		setDelilahWorking(true)

		const data = {
			command: valCommand,
			text_field_value: valTextFieldValue,
			thread_id: selectedThread,
		}
		socket.send(JSON.stringify({ ...data, websocket_type: "command" }))
	}

	const regenerateGeneration = (e) => {
		setDelilahWorking(true)
		setThreads(
			threads.map((thread) => {
				if (thread.id === selectedThread) {
					return {
						...thread,
						generations: [
							...thread.generations.slice(
								0,
								thread.generations.length - 1
							),
						],
					}
				}
				return thread
			})
		)

		const toSend = { ...formData, websocket_type: "regenerate" }
		socket.send(JSON.stringify(toSend))
	}

	const deleteGeneration = (e) => {
		setDelilahWorking(true)

		const deleteRequest = new Request(
			"threads/" + selectedThread + "/delete_last_generation/",
			{
				method: "POST",
			}
		)

		deleteRequest
			.then((response) => {
				setThreads(
					threads.map((thread) => {
						if (thread.id === selectedThread) {
							return response.data
						}
						return thread
					})
				)
				setDelilahWorking(false)
			})
			.catch((err) => {
				console.log(err)
				setDelilahWorking(false)
			})
	}

	return (
		<div className="flex flex-col bg-slate-100 h-screen w-4/6">
			<div className="overflow-y-scroll overflow-x-hidden h-screen bg-slate-100">
				{threads.find((thread) => thread.id === selectedThread)
					.generations.length === 0 ? (
					<EmptyThreadInfo />
				) : (
					<div>
						{threads
							.find((thread) => thread.id === selectedThread)
							.generations.map((generation, index, arr) => {
								return (
									<div key={index}>
										<Message
											text={generation.prompt.user_string}
											author="You"
											sendCommands={sendCommands}
											delilahWorking={delilahWorking}
											setDelilahWorking={
												setDelilahWorking
											}
											snippets={snippets}
											setSnippets={setSnippets}
										/>
										<Message
											text={generation.text}
											author="Delilah"
											sendCommands={sendCommands}
											delilahWorking={delilahWorking}
											setDelilahWorking={
												setDelilahWorking
											}
											snippets={snippets}
											setSnippets={setSnippets}
										/>
									</div>
								)
							})}
						<div
							ref={(el) => {
								if (el) {
									el.scrollIntoView(false)
								}
							}}
						/>
					</div>
				)}
			</div>

			<CenterForm
				threads={threads}
				selectedThread={selectedThread}
				formData={formData}
				handleChangeForm={handleChangeForm}
				command={command}
				sendForm={sendForm}
				handleChange={handleChange}
				showTextField={showTextField}
				textFieldValue={textFieldValue}
				setTextFieldValue={setTextFieldValue}
				sendCommands={sendCommands}
				deleteGeneration={deleteGeneration}
				regenerateGeneration={regenerateGeneration}
				delilahWorking={delilahWorking}
			/>
		</div>
	)
}

export default Center
