import "./style.css"
import { Fragment, useState } from "react"
import { Listbox, Transition } from "@headlessui/react"
import { ChevronDownIcon } from "@heroicons/react/24/solid"

function classNames(...classes) {
	return classes.filter(Boolean).join(" ")
}

function capitalizeFirstLetter(str) {
	return str.charAt(0).toUpperCase() + str.slice(1)
}

const ModeSelector = ({ selectedMode, setSelectedMode }) => {
	const options = ["chat", "freewrite"]

	return (
		<div className='flex flex-row mt-4 w-3/4 text-white align-center items-center justify-center'>
			<div
				className={
					"p-3 rounded-tl-xl rounded-bl-xl hover:cursor-pointer w-1/2 text-center" +
					(selectedMode === "chat" ? " bg-[#7D66D7]" : " bg-delilahTertiary")
				}
				onClick={() => setSelectedMode("chat")}
			>
				Chat
			</div>
			<div
				className={
					"p-3 rounded-tr-xl rounded-br-xl hover:cursor-pointer w-1/2 text-center" +
					(selectedMode === "freewrite"
						? " bg-[#7D66D7]"
						: " bg-delilahTertiary")
				}
				onClick={() => setSelectedMode("freewrite")}
			>
				Freewrite
			</div>
		</div>
	)
}

export default ModeSelector
