import { useEffect, useState, useRef } from "react"
import Request from "../../Request/index.jsx"
import Menu from "@mui/material/Menu"
import MenuItem from "@mui/material/MenuItem"
import ListItemText from "@mui/material/ListItemText"
import Typography from "@mui/material/Typography"
import { useAuth0 } from "@auth0/auth0-react"
// import logo from "../../../img/logo.png"
import CenterMenu from "../CenterMenu"

const Message = ({
	snippets,
	setSnippets,
	sendCommand,
	writtenByDelilah,
	text,
	delilahWorking,
	isLast,
	freewriteThreads,
	setFreewriteThreads,
	setSelectedFreewriteThread,
	setShowCTASubscribed,
	setSelectedMode,
}) => {
	const [contextMenu, setContextMenu] = useState(null)
	const { user } = useAuth0()
	const { picture } = user
	const elementRef = useRef(null)

	useEffect(() => {
		function handleSelectionChange(e) {
			const selection = window.getSelection().toString()
			e.preventDefault()

			if (selection.length > 5) {
				setContextMenu(
					contextMenu === null
						? {
								mouseX: e.clientX + 2,
								mouseY: e.clientY - 6,
						  }
						: null
				)
			} else {
				setContextMenu(null)
			}
		}

		if (elementRef.current) {
			elementRef.current.addEventListener(
				"mouseup",
				handleSelectionChange
			)
		}

		return () => {
			if (elementRef.current) {
				elementRef.current.removeEventListener(
					"mouseup",
					handleSelectionChange
				)
			}
		}
	}, [contextMenu, elementRef, text])

	const handleContextMenu = (event) => {
		event.preventDefault()
	}

	const handleCopy = () => {
		navigator.clipboard.writeText(window.getSelection().toString())
		setContextMenu(null)
	}

	const handleAddToSnippet = () => {
		const parameters = {
			data: { title: "", text: window.getSelection().toString() },
			method: "POST",
		}
		const snippetsRequest = new Request("snippets/", parameters)
		snippetsRequest
			.then((res) => {
				setSnippets([...snippets, res.data])
			})
			.catch((err) => {
				console.log(err)
			})
		setContextMenu(null)
	}

	const handleShowDontTell = (e) => {
		e.preventDefault()
		sendCommand("showdonttell", window.getSelection().toString())
		setContextMenu(null)
	}

	const handleRemove = (e) => {
		e.preventDefault()
		sendCommand("remove", window.getSelection().toString())
		setContextMenu(null)
	}

	const handleOpenInFreewrite = (e) => {
		const threadRequest = new Request("freewritethreads/", {
			method: "POST",
			data: {
				title: "New freewrite thread! ðŸŽ‰",
				description: "This is the new thread's description",
				text: window.getSelection().toString(),
			},
		})
		threadRequest
			.then((res) => {
				setFreewriteThreads([...freewriteThreads, res.data])
				setSelectedFreewriteThread(res.data.id)
				setSelectedMode("freewrite")
				setContextMenu(null)
			})
			.catch((err) => {
				setShowCTASubscribed(true)
			})
	}

	return (
		<div className="relative">
			<div
				className={
					"flex flex-row mb-4 pl-6 " +
					(writtenByDelilah ? "bg-white " : " ") +
					(isLast
						? "mb-[300px] border-l-4 border-delilahSecondary shadow-lg"
						: " ") +
					(!isLast ? "rounded-xl " : " ")
				}
				ref={elementRef}
			>
				{!writtenByDelilah && (
					<img
						src={picture}
						alt="PFP"
						className="h-8 w-8 rounded-2xl mt-6"
					/>
				)}
				{writtenByDelilah && (
					<img
						className="h-8 w-8 my-6"
						src={logo}
						alt="Delilah Logo"
					/>
				)}

				<div className="flex flex-col items-start w-full p-3 items-center justify-center">
					<div className={`text-black w-full p-3 rounded-md`}>
						<div
							className="whitespace-pre-wrap leading-7 hover:cursor-text relative"
							onContextMenu={handleContextMenu}
						>
							{text.trim()}

							<Menu
								open={contextMenu !== null}
								onClose={() => setContextMenu(null)}
								anchorReference="anchorPosition"
								anchorPosition={
									contextMenu !== null
										? {
												top: contextMenu.mouseY,
												left: contextMenu.mouseX,
										  }
										: undefined
								}
							>
								<MenuItem onClick={handleCopy}>
									<ListItemText>Copy</ListItemText>
									<Typography
										variant="body2"
										color="text.secondary"
									>
										âŒ˜C
									</Typography>
								</MenuItem>
								<MenuItem
									onClick={handleAddToSnippet}
									disabled={
										!(
											window.getSelection().toString()
												.length > 0
										) || delilahWorking
									}
								>
									Add to Snippet
								</MenuItem>
								<MenuItem
									onClick={handleOpenInFreewrite}
									disabled={
										!(
											window.getSelection().toString()
												.length > 0
										) || delilahWorking
									}
								>
									Open in Freewrite
								</MenuItem>
								<MenuItem
									onClick={handleShowDontTell}
									disabled={
										!(
											window.getSelection().toString()
												.length > 0
										) || delilahWorking
									}
								>
									Show Don't Tell
								</MenuItem>
								<MenuItem
									onClick={handleRemove}
									disabled={
										!(
											window.getSelection().toString()
												.length > 0
										) || delilahWorking
									}
								>
									Remove
								</MenuItem>
							</Menu>
						</div>
					</div>
				</div>
			</div>
		</div>
	)
}
export default Message
