import React, { useState, useEffect } from "react"
import ReactQuill from "react-quill"
import "react-quill/dist/quill.snow.css"
import Request from "../Request"
import { useRef } from "react"
import {
	ArrowPathIcon,
	ArrowTrendingUpIcon,
	ScissorsIcon,
	SparklesIcon,
} from "@heroicons/react/20/solid"
const FreeWrite = ({
	freewriteThreads,
	setFreewriteThreads,
	selectedFreewriteThread,
	socket,
}) => {
	const [text, setText] = useState(
		freewriteThreads.find(
			(freewriteThread) => freewriteThread.id === selectedFreewriteThread
		).text
	)
	const [bounds, setBounds] = useState(null)
	const [showMenu, setShowMenu] = useState(false)
	const [isSaving, setIsSaving] = useState(false)
	const [isGenerating, setIsGenerating] = useState(false)
	const [insertAt, setInsertAt] = useState(0)
	const quillRef = useRef(null)

	useEffect(() => {
		if (socket === null) return

		socket.onmessage = (e) => {
			const data = JSON.parse(e.data)
			console.log(data)
			const editor = quillRef.current.getEditor()
			if (data["first_output"]) {
				const selection = editor.getSelection(true)
				editor.deleteText(selection.index, selection.length)
			}

			if (data["output_text"]) {
				editor.insertText(insertAt, data.output_text)
				setInsertAt(insertAt + data.output_text.length)
			}

			if (data["status"] === "DONE") {
				setIsGenerating(false)
			}
		}

		return () => {
			socket.onmessage = null
		}
	}, [insertAt])

	useEffect(() => {
		setText(
			freewriteThreads.find(
				(freewriteThread) => freewriteThread.id === selectedFreewriteThread
			).text
		)
	}, [selectedFreewriteThread])

	useEffect(() => {
		const parameters = {
			data: {
				text: text,
			},
			method: "POST",
		}
		const updateTextRequest = new Request(
			"freewritethreads/" + selectedFreewriteThread + "/settext/",
			parameters
		)
		setIsSaving(true)

		updateTextRequest
			.then((res) => {
				const newFreewriteThreads = freewriteThreads.map((freewriteThread) => {
					if (freewriteThread.id === selectedFreewriteThread) {
						freewriteThread.text = text
					}
					return freewriteThread
				})
				setFreewriteThreads(newFreewriteThreads)
				setIsSaving(false)
			})
			.catch((err) => {
				console.log(err)
			})
	}, [text])

	const modules = {
		toolbar: [
			[{ header: [1, 2, false] }],
			["bold", "italic", "underline", "strike", "blockquote"],
			[
				{ list: "ordered" },
				{ list: "bullet" },
				{ indent: "-1" },
				{ indent: "+1" },
			],
			["link", "image"],
		],
	}

	const handleChangeSelection = (e) => {
		if (!e || e.length === 0) {
			setShowMenu(false)
			return
		}

		const selectedText = window.getSelection().toString()
		if (!selectedText) {
			setShowMenu(false)
			return
		}

		const editor = quillRef.current.getEditor()
		const selectionBounds = editor.getBounds(e.index, e.length)
		setBounds(selectionBounds)
		setShowMenu(true)
	}

	const handleContextMenu = (e, mode) => {
		e.preventDefault()

		setIsGenerating(true)

		const editor = quillRef.current.getEditor()
		const selection = editor.getSelection(true)
		setInsertAt(selection.index)

		socket.send(
			JSON.stringify({
				input_text: window.getSelection().toString(),
				mode: mode,
				websocket_type: "freewritesnippet",
				freewrite_thread_id: selectedFreewriteThread,
			})
		)
	}

	const menuOption = ({ mode, icon, text }) => {
		return (
			<div
				className='p-2 text-sm font-medium w-full hover:cursor-pointer hover:bg-delilahPrimary/50 flex flex-row text-[#475467]'
				onMouseDown={(e) => handleContextMenu(e, mode)}
			>
				{icon}
				<p className='ml-2 '>{text}</p>
			</div>
		)
	}

	return (
		<div className='w-5/6 overflow-y-scroll overflow-x-hidden flex flex-col h-screen grow p-10'>
			<div className='relative'>
				{/* {isSaving && <p>Currently Saving...</p>} */}

				<ReactQuill
					theme='snow'
					value={text}
					onChange={setText}
					modules={modules}
					onChangeSelection={(e) => handleChangeSelection(e)}
					ref={quillRef}
					readOnly={isGenerating}
				/>

				<div
					className={
						"flex flex-col bg-[#FFFFFF] rounded absolute space-x-1 text-black items-center justify-center border border-slate-200" +
						(showMenu ? "" : " hidden")
					}
					style={
						bounds
							? {
									top: bounds.top,
									left: bounds.left,
									minWidth: 100,
									maxWidth: 200,
							  }
							: {}
					}
				>
					<p className='font-normal text-xs text-left text-slate-500 mt-2 self-start ml-2'>
						Delilah Options
					</p>
					{menuOption({
						mode: "flowery",
						icon: <SparklesIcon className='h-5 w-5 text-violet-700' />,
						text: "Show Don't Tell",
					})}
					{menuOption({
						mode: "expand",
						icon: <ArrowTrendingUpIcon className='h-5 w-5 text-green-700' />,
						text: "Expand",
					})}
					{menuOption({
						mode: "summarize",
						icon: <ScissorsIcon className='h-5 w-5 text-red-700' />,
						text: "Summarize",
					})}
					{menuOption({
						mode: "regenerate",
						icon: <ArrowPathIcon className='h-5 w-5 text-yellow-500' />,
						text: "Regenerate",
					})}
				</div>
			</div>
		</div>
	)
}

export default FreeWrite
