import "./style.css"
import Request from "../../Request/index.jsx"
import { PlusIcon } from "@heroicons/react/24/outline"

const NewThreadButton = ({
	threads,
	setThreads,
	setSelectedThread,
	setShowCTASubscribed,
	setShowChatModal,
}) => {
	const submitNewThread = () => {
		const threadRequest = new Request("threads/", {
			method: "POST",
			data: {
				title: "New chat! ðŸŽ‰",
				description: "This is the new thread's description",
			},
		})
		threadRequest
			.then((res) => {
				setThreads([...threads, res.data])
				setSelectedThread(res.data.id)
			})
			.catch((err) => {
				setShowCTASubscribed(true)
			})
	}

	return (
		<div
			className='flex flex-row justify-center items-center bg-delilahSecondary w-3/4 mt-5 rounded-[0.5rem] p-3 hover:cursor-pointer'
			onClick={() => setShowChatModal(true)}
		>
			<PlusIcon className='h-5 w-5 text-white mr-2' aria-hidden='true' />
			<p className='text-white font-normal'>New Chat</p>
		</div>
	)
}

export default NewThreadButton
