import React, { useState, useEffect } from "react"
import Snippet from "./Snippet"
import fuzzy from "fuzzy"
import Request from "../Request/index.jsx"
import SnippetSearchBar from "./SnippetSearchBar"
import NewSnippetButton from "./NewSnippetButton"

const Right = ({ snippets, setSnippets }) => {
	const [searchTerm, setSearchTerm] = useState("")
	const [searchResults, setSearchResults] = useState(snippets)

	useEffect(() => {
		if (searchTerm === "") {
			setSearchResults(snippets)
			return
		} else {
			const searchResult = fuzzy.filter(searchTerm, snippets, {
				extract: (el) => el.text,
			})

			const results = searchResult.map((el) => el.original)
			setSearchResults(results)
			return
		}
	}, [snippets, searchTerm])

	const handleDrop = (e) => {
		e.preventDefault()

		const parameters = {
			data: { text: e.dataTransfer.getData("text") },
			method: "POST",
		}
		const snippetsRequest = new Request("snippets/", parameters)
		snippetsRequest
			.then((res) => {
				setSnippets([...snippets, res.data])
			})
			.catch((err) => {
				console.log(err)
			})
	}

	const handleOnPaste = (e) => {
		const text = e.clipboardData.getData("text")
		const parameters = {
			data: { text: text },
			method: "POST",
		}

		const snippetsRequest = new Request("snippets/", parameters)
		snippetsRequest
			.then((res) => {
				setSnippets([...snippets, res.data])
			})
			.catch((err) => {
				console.log(err)
			})
	}

	const handleDragOver = (e) => {
		e.stopPropagation()
		e.preventDefault()
	}

	const handleDragEnter = (e) => {
		e.preventDefault()
	}

	return (
		<div
			className='overflow-y-scroll overflow-x-hidden flex flex-col w-1/6 h-screen bg-[#170a35]'
			onDragOver={(e) => handleDragOver(e)}
			onDrop={(e) => handleDrop(e)}
			onDragEnter={(e) => handleDragEnter(e)}
			onPaste={(e) => handleOnPaste(e)}
		>
			<SnippetSearchBar
				onChange={(e) => setSearchTerm(e.target.value)}
			></SnippetSearchBar>
			<NewSnippetButton
				snippets={snippets}
				setSnippets={setSnippets}
			></NewSnippetButton>

			<div className='flex flex-col items-center pb-4'>
				{searchResults.map((snippet, index) => {
					return (
						<Snippet
							key={index}
							snippet={snippet}
							index={index}
							snippets={snippets}
							setSnippets={setSnippets}
						></Snippet>
					)
				})}
			</div>
		</div>
	)
}

export default Right
