import PaperPlane from "../../../svg/paperplane.svg"
import TextareaAutosize from "react-textarea-autosize"

const BrainstormForm = ({
	commandValue,
	setCommandValue,
	sendCommand,
	formSubmitting,
	setFormSubmitting,
}) => {
	const submitForm = (e) => {
		e.preventDefault()
		setFormSubmitting(true)
		sendCommand(e)
	}

	return (
		<form
			onSubmit={submitForm}
			className='rounded-2xl bg-white w-3/5 absolute mx-auto left-0 right-0 bottom-8 drop-shadow-2xl'
		>
			<div className='px-4 flex flex-row justify-center items-center w-full'>
				<div className='w-full flex flex-col p-3'>
					<TextareaAutosize
						className='flex-2 font-md p-2 focus:outline-none focus:ring-none focus:border-none'
						type='text'
						value={commandValue}
						onChange={(e) => setCommandValue(e.target.value)}
						placeholder='Talk to Delilah...'
						disabled={formSubmitting}
						maxRows={3}
						onKeyDown={(e) => {
							if (e.key === "Enter") {
								submitForm(e)
							}
						}}
					/>
				</div>

				<button className='bg-[#7C55D6] p-3 rounded-lg' type='submit'>
					<img src={PaperPlane} alt='paperplane' className='paperplane' />
				</button>
			</div>
		</form>
	)
}

export default BrainstormForm
