import "./Content.css"
import Left from "./Left"
import Center from "./Center"
import Right from "./Right"
import React, { useEffect, useState } from "react"
import Spinner from "./Spinner/spinner"
import Request from "./Request/index.jsx"

const Content = () => {
	const [threads, setThreads] = useState([])
	const [selectedThread, setSelectedThread] = useState("")
	const [snippets, setSnippets] = useState([])

	useEffect(() => {
		// sleep for 1 second to simulate loading
		setTimeout(() => {
			initData()
		}, 1000)
	}, [])

	const initData = () => {
		const threadsRequest = new Request("threads/")
		threadsRequest
			.then((res) => {
				setThreads(res.data)
				setSelectedThread(res.data[0].id)
			})
			.catch((err) => {
				console.log(err)
			})

		const snippetsRequest = new Request("snippets/")
		snippetsRequest
			.then((res) => {
				setSnippets(res.data)
			})
			.catch((err) => {
				console.log(err)
			})
	}

	if (selectedThread === "") {
		return <Spinner />
	}

	return (
		<div className="content">
			<Left
				threads={threads}
				setThreads={setThreads}
				selectedThread={selectedThread}
				setSelectedThread={setSelectedThread}
			/>
			<Center
				threads={threads}
				setThreads={setThreads}
				selectedThread={selectedThread}
				setSelectedThread={setSelectedThread}
				snippets={snippets}
				setSnippets={setSnippets}
			/>
			<Right snippets={snippets} setSnippets={setSnippets} />
		</div>
	)
}

export default Content
