import "bulma/css/bulma.min.css"
import { useAuth0 } from "@auth0/auth0-react"
import Cookies from "universal-cookie"
import "./style.css"
import { useEffect } from "react"
import Request from "../Request/index.jsx"

const NavTray = () => {
	useEffect(() => {
		// Check to see if this is a redirect back from Checkout
		const query = new URLSearchParams(window.location.search)

		if (query.get("success")) {
			alert(`Order processed! You will receive an email confirmation.`)
		}

		if (query.get("cancelled")) {
			alert(
				`Order canceled -- continue to shop around and checkout when you're ready.`
			)
		}
	}, [])

	const { logout } = useAuth0()
	const cookies = new Cookies()

	// make an async function that clears the cookies, which can be chained with a .then to call logout later
	const clearCookies = async () => {
		await cookies.remove("token")
	}

	const handleCheckout = (e) => {
		const commandsRequest = new Request("stripe/create-checkout-session/", {
			method: "POST",
		})

		commandsRequest
			.then((response) => {
				console.log(response)
			})
			.catch((err) => {
				console.log(err)
			})
	}

	return (
		<div className="navbtn-tray">
			<div className="purple-line-break"></div>
			<div className="nav-button">
				<i className="fa-solid fa-up-right-from-square"></i>
				<span>&nbsp;</span>
				Guides and FAQ
			</div>
			<div className="nav-button" onClick={handleCheckout}>
				<i className="fa-solid fa-credit-card"></i>
				<span>&nbsp;</span>
				Upgrade
			</div>
			<div href="/settings" className="nav-button">
				<i className="fa-solid fa-cog "></i>
				<span>&nbsp;</span>
				Settings
			</div>
			<div
				className="nav-button"
				onClick={() => {
					clearCookies().then(() =>
						logout({ returnTo: window.location.origin })
					)
				}}
			>
				<i className="fa-solid fa-sign-out"></i>
				<span>&nbsp;</span>
				Log Out
			</div>
		</div>
	)
}

export default NavTray
