import "./style.css"
import { useState } from "react"
import Request from "../Request/index.jsx"
import Modal from "@mui/material/Modal"
import TextareaAutosize from "react-textarea-autosize"

const Snippet = (props) => {
	const [open, setOpen] = useState(false)
	const handleOpen = () => setOpen(true)
	const handleClose = () => setOpen(false)
	const [editedSnippetValue, setEditedSnippetValue] = useState(
		props.snippet.text
	)

	const deleteSnippet = (e) => {
		const snippetsRequest = new Request(
			"snippets/" + props.snippet.id + "/",
			{
				method: "DELETE",
			}
		)
		snippetsRequest
			.then((res) => {
				const newSnippets = [...props.snippets]
				newSnippets.splice(props.index, 1)
				props.setSnippets(newSnippets)
			})
			.catch((err) => {
				console.log(err)
			})
	}

	const handleModalSubmit = (e) => {
		e.preventDefault()

		const snippetsRequest = new Request(
			"snippets/" + props.snippet.id + "/edit/",
			{
				method: "POST",
				data: { text: editedSnippetValue },
			}
		)
		snippetsRequest
			.then((res) => {
				props.setSnippets(
					props.snippets.map((snippet) => {
						if (snippet.id === props.snippet.id) {
							snippet.text = editedSnippetValue
							return snippet
						}
						return snippet
					})
				)
				handleClose()
			})
			.catch((err) => {
				console.log(err)
			})
	}

	return (
		<div className="snippet" key={props.index}>
			<div className="options-menu">
				<i
					className="fa-solid fa-pencil edit-icon"
					onClick={handleOpen}
				></i>
				<i
					className="fa-solid fa-trash delete-icon"
					onClick={(e) => deleteSnippet(e)}
				></i>
			</div>
			{open ? (
				<form
					className="snippet-text-edit-container"
					onSubmit={handleModalSubmit}
				>
					<TextareaAutosize
						className="snippet-text-edit-area"
						value={editedSnippetValue}
						onChange={(e) => setEditedSnippetValue(e.target.value)}
						autoFocus
					></TextareaAutosize>
					<button
						className="snippet-text-edit-done-button"
						type="submit"
					>
						Done
					</button>
				</form>
			) : (
				<p className="snippet-text">{props.snippet.text}</p>
			)}
		</div>
	)
}
export default Snippet
