import React, { useState } from "react"
import Request from "../../Request/index.jsx"
import "./new-snippet-button.css"
import TextareaAutosize from "react-textarea-autosize"

const NewSnippetButton = ({ snippets, setSnippets }) => {
	const [showNewSnippet, setShowNewSnippet] = useState(false)
	const [titleFieldValue, setTitleFieldValue] = useState("")
	const [textFieldValue, setTextFieldValue] = useState("")

	const handleTextFieldSubmit = (e) => {
		e.preventDefault()

		const snippetsRequest = new Request("snippets/", {
			method: "POST",
			data: { title: titleFieldValue, text: textFieldValue },
		})

		snippetsRequest
			.then((res) => {
				console.log(
					"NewSnippetButton.handleTextFieldSubmit: res.data = ",
					res.data
				)
				setSnippets([...snippets, res.data])
				setTitleFieldValue("")
				setTextFieldValue("")
				setShowNewSnippet(false)
			})
			.catch((err) => {
				console.log(err)
			})
	}

	const handleTextFieldChange = (e) => {
		if (e.key === "Enter") {
			setSnippets([...snippets, textFieldValue])
			setTextFieldValue("")
			setShowNewSnippet(false)
		} else {
			setTextFieldValue(e.target.value)
		}
	}

	return (
		<div className='new-snippet-button-container'>
			<button
				onClick={() => {
					setShowNewSnippet(true)
				}}
				className='new-snippet-button'
				hidden={showNewSnippet}
			>
				<i className='fa-solid fa-plus fa-lg plus-icon'></i>
				<span>&nbsp;</span>
				Take a snippet...
			</button>

			{showNewSnippet && (
				<div className='snippet'>
					<div className='snippet-header'>
						<input
							className='snippet-title-edit'
							id='snippet-title-edit'
							type='text'
							value={titleFieldValue}
							onChange={(e) => {
								setTitleFieldValue(e.target.value)
							}}
							placeholder='Title'
						/>
					</div>

					<form
						className='snippet-text-edit-container'
						onSubmit={handleTextFieldSubmit}
					>
						<TextareaAutosize
							className='snippet-text-edit-area'
							id='snippet-text-edit-area'
							type='text'
							value={textFieldValue}
							onChange={handleTextFieldChange}
							autoFocus
							onFocus={function (e) {
								var val = e.target.value
								e.target.value = ""
								e.target.value = val
							}}
							placeholder='Body'
						/>
						<button className='snippet-text-edit-done-button' type='submit'>
							Done
						</button>
					</form>
				</div>
			)}
		</div>
	)
}

export default NewSnippetButton
