import React, { useEffect, useState } from "react"
import Left from "./Left"
import Center from "./Center"
import Right from "./Right"
import Spinner from "./Spinner"
import Request from "./Request"
import FreeWrite from "./FreeWrite"
import Settings from "./Settings"
import Cookies from "universal-cookie"
import CTASubscribeModal from "./CTASubscribeModal"
import Brainstorm from "./Brainstorm"
import BrainstormInputModal from "./Brainstorm/BrainstormInputModal"
import ChatModal from "./Center/ChatModal"
import { useAuth0 } from "@auth0/auth0-react"

const cookies = new Cookies()

const Content = () => {
	// auth0 stuff, get name, email address
	const { user } = useAuth0()

	// which mode a user is in
	const [selectedMode, setSelectedMode] = useState("freewrite")

	// brainstorm mode
	const [brainstormThreads, setBrainstormThreads] = useState([])
	const [selectedBrainstormThread, setSelectedBrainstormThread] = useState("")

	// chat mode
	const [threads, setThreads] = useState([])
	const [selectedThread, setSelectedThread] = useState("")
	const [snippets, setSnippets] = useState([])

	// freewrite mode
	const [freewriteThreads, setFreewriteThreads] = useState([])
	const [selectedFreewriteThread, setSelectedFreewriteThread] = useState("")

	// user info
	const [userID, setUserID] = useState("")
	const [subscription, setSubscription] = useState({})

	// close navbar
	const [showNav, setShowNav] = useState(true)

	// websocket connection
	const [socket, setSocket] = useState(null)
	const [reconnectSocket, setReconnectSocket] = useState(false)

	// show CTA subscribed modal
	const [showCTASubscribed, setShowCTASubscribed] = useState(false)

	// show brainstorm input modal
	const [showBrainstormModal, setShowBrainstormModal] = useState(false)

	// show new chat modal
	const [showChatModal, setShowChatModal] = useState(false)

	useEffect(() => {
		if (cookies.get("token") === undefined) {
			console.log("Cookies: ", cookies.getAll())
			setTimeout(() => {}, 1000)
		}

		// userflow.init(process.env.REACT_APP_USERFLOW_TOKEN)
		// userflow.identify(user.email, {
		// 	name: user.name,
		// 	email: user.email,
		// 	// set onboarded_brainstorm to false only if the user hasn't had it set before
		// 	onboarded_brainstorm: { set_once: "false" },
		// 	onboarded_generate: { set_once: "false" },
		// })
		// cookies.set("userflow", true, { path: "/" })

		initData()

		setSocket(
			new WebSocket(
				process.env.REACT_APP_WS_URL +
					"ws/essay/" +
					"?token=" +
					cookies.get("token")
			)
		)
		setReconnectSocket(!reconnectSocket)
	}, [])

	useEffect(() => {
		if (socket === null) return
		socket.onopen = (e) => {
			console.log("Connected to websocket from home")
		}

		socket.onclose = (e) => {
			console.log("Disconnected from websocket from home because: ", e)
			setTimeout(() => {
				setSocket(connectToSocket())
				setReconnectSocket(!reconnectSocket)
			}, 1000)
		}

		socket.onerror = (e) => {
			console.log("Error connecting to websocket from home")
			socket.close()
		}

		return () => {
			socket.close()
		}
	}, [reconnectSocket])

	const connectToSocket = () => {
		const newWS = new WebSocket(
			process.env.REACT_APP_WS_URL +
				"ws/essay/" +
				"?token=" +
				cookies.get("token")
		)
		return newWS
	}

	const initData = () => {
		const meRequest = new Request("users/me/")
		meRequest
			.then((res) => {
				setUserID(res.data.id)
				setSubscription(res.data.subscription)
			})
			.catch((err) => {
				console.log(err)
			})

		const brainstormThreadsRequest = new Request("brainstormthreads/")
		brainstormThreadsRequest
			.then((res) => {
				setBrainstormThreads(res.data)
				setSelectedBrainstormThread(res.data[0].id)
			})
			.catch((err) => {
				console.log(err)
			})

		const threadsRequest = new Request("threads/")
		threadsRequest
			.then((res) => {
				setThreads(res.data)
				setSelectedThread(res.data[0].id)
			})
			.catch((err) => {
				console.log(err)
			})

		const freewriteThreadsRequest = new Request("freewritethreads/")
		freewriteThreadsRequest
			.then((res) => {
				setFreewriteThreads(res.data)
				setSelectedFreewriteThread(res.data[0].id)
			})
			.catch((err) => {
				console.log(err)
			})

		const snippetsRequest = new Request("snippets/")
		snippetsRequest
			.then((res) => {
				setSnippets(res.data)
			})
			.catch((err) => {
				console.log(err)
			})
	}

	if (selectedThread === "") {
		return <Spinner />
	}

	return (
		<div className="flex flex-row from-[#F5F4FF] to-[#F5F4FF] bg-gradient-135">
			<CTASubscribeModal
				open={showCTASubscribed}
				setShowCTASubscribed={setShowCTASubscribed}
				setSelectedMode={setSelectedMode}
			/>

			<BrainstormInputModal
				open={showBrainstormModal}
				setOpen={setShowBrainstormModal}
				brainstormThreads={brainstormThreads}
				setBrainstormThreads={setBrainstormThreads}
				selectedBrainstormThread={selectedBrainstormThread}
				setSelectedBrainstormThread={setSelectedBrainstormThread}
				socket={socket}
			/>

			<ChatModal
				open={showChatModal}
				setOpen={setShowChatModal}
				threads={threads}
				setThreads={setThreads}
				selectedThread={selectedThread}
				setSelectedThread={setSelectedThread}
				socket={socket}
				setShowCTASubscribed={setShowCTASubscribed}
			/>

			<Left
				selectedMode={selectedMode}
				setSelectedMode={setSelectedMode}
				showNav={showNav}
				// chat mode
				threads={threads}
				setThreads={setThreads}
				selectedThread={selectedThread}
				setSelectedThread={setSelectedThread}
				setShowChatModal={setShowChatModal}
				// freewrite mode
				freewriteThreads={freewriteThreads}
				setFreewriteThreads={setFreewriteThreads}
				selectedFreewriteThread={selectedFreewriteThread}
				setSelectedFreewriteThread={setSelectedFreewriteThread}
				// brainstorm mode
				brainstormThreads={brainstormThreads}
				setBrainstormThreads={setBrainstormThreads}
				selectedBrainstormThread={selectedBrainstormThread}
				setSelectedBrainstormThread={setSelectedBrainstormThread}
				setShowBrainstormModal={setShowBrainstormModal}
				// show the subscribed button
				setShowCTASubscribed={setShowCTASubscribed}
				// subscribed status
				subscription={subscription}
			/>

			{selectedMode === "chat" && (
				<>
					<Center
						threads={threads}
						setThreads={setThreads}
						selectedThread={selectedThread}
						setSelectedThread={setSelectedThread}
						snippets={snippets}
						setSnippets={setSnippets}
						socket={socket}
						setShowCTASubscribed={setShowCTASubscribed}
						freewriteThreads={freewriteThreads}
						setFreewriteThreads={setFreewriteThreads}
						setSelectedFreewriteThread={setSelectedFreewriteThread}
						setSelectedMode={setSelectedMode}
					/>
					{/* <Right snippets={snippets} setSnippets={setSnippets} /> */}
				</>
			)}

			{selectedMode === "freewrite" && (
				<FreeWrite
					freewriteThreads={freewriteThreads}
					setFreewriteThreads={setFreewriteThreads}
					selectedFreewriteThread={selectedFreewriteThread}
					setSelectedFreewriteThread={setSelectedFreewriteThread}
					socket={socket}
					setShowCTASubscribed={setShowCTASubscribed}
				/>
			)}

			{selectedMode === "brainstorm" && (
				<Brainstorm
					brainstormThreads={brainstormThreads}
					setBrainstormThreads={setBrainstormThreads}
					selectedBrainstormThread={selectedBrainstormThread}
					setSelectedBrainstormThread={setSelectedBrainstormThread}
					socket={socket}
					setShowCTASubscribed={setShowCTASubscribed}
				/>
			)}

			{selectedMode === "settings" && (
				<Settings subscription={subscription} userID={userID} />
			)}
		</div>
	)
}

export default Content
