import React, { useState } from "react"
import "bulma/css/bulma.min.css"
import "./style.css"
import Request from "../Request/index.jsx"

function ThreadButton(props) {
	const [showTextField, setShowTextField] = useState(false)
	const [textFieldValue, setTextFieldValue] = useState(props.thread.title)

	const deleteThread = (e) => {
		e.preventDefault()

		const threadRequest = new Request("threads/" + e.target.id, {
			method: "DELETE",
		})
		threadRequest
			.then((res) => {
				props.setThreads(
					props.threads.filter((thread) => thread.id !== e.target.id)
				)
				props.setSelectedThread(props.threads[0].id)
			})
			.catch((err) => {
				console.log(err)
			})
	}

	const handleTextFieldSubmit = (e) => {
		e.preventDefault()

		const threadRequest = new Request("threads/" + props.thread.id + "/edit/", {
			method: "POST",
			data: { title: textFieldValue },
		})
		threadRequest
			.then((res) => {
				props.setThreads(
					props.threads.map((thread) => {
						if (thread.id === props.thread.id) {
							thread.title = textFieldValue
						}
						return thread
					})
				)
				setShowTextField(false)
			})
			.catch((err) => {
				console.log(err)
			})
	}

	const handleSelectThread = (e) => {
		if (e.target.id) {
			props.setSelectedThread(e.target.id)
		} else if (e.target.parentElement.id) {
			props.setSelectedThread(e.target.parentElement.id)
		}
	}

	return (
		<div
			className={
				"thread-button " +
				(props.selectedThread === props.thread.id ? "thread-selected" : "")
			}
			id={props.thread.id}
			onClick={handleSelectThread}
		>
			<i className='fa-solid fa-message'></i>
			{!showTextField ? (
				<>
					<p className='thread-title' id={props.thread.id}>
						{props.thread.title}
					</p>

					{props.selectedThread === props.thread.id && (
						<div className='thread-buttons'>
							<i
								className='fa-solid fa-pencil'
								id={props.thread.id}
								onClick={(e) => setShowTextField(true)}
							></i>
							<i
								className='fa-solid fa-trash'
								id={props.thread.id}
								onClick={(e) => deleteThread(e)}
							></i>
						</div>
					)}
				</>
			) : (
				<div className='thread-button'>
					<form className='thread-rename' onSubmit={handleTextFieldSubmit}>
						<input
							type='text'
							value={textFieldValue}
							onChange={(e) => setTextFieldValue(e.target.value)}
							autoFocus
							onFocus={(e) => {
								var val = e.target.value
								e.target.value = ""
								e.target.value = val
							}}
						/>
					</form>
				</div>
			)}
		</div>
	)
}

export default ThreadButton
