import React, { useState } from "react"
import Request from "../Request/index.jsx"
import "./new-snippet-button.css"
import TextareaAutosize from "react-textarea-autosize"

const NewSnippetButton = (props) => {
	const [showNewSnippet, setShowNewSnippet] = useState(false)
	const [titleFieldValue, setTitleFieldValue] = useState("")
	const [textFieldValue, setTextFieldValue] = useState("")

	const handleTextFieldSubmit = (e) => {
		e.preventDefault()

		const snippetsRequest = new Request("snippets/", {
			method: "POST",
			data: { text: textFieldValue },
		})
		snippetsRequest
			.then((res) => {
				props.setSnippets([...props.snippets, res.data])
				setTextFieldValue("")
				setShowNewSnippet(false)
			})
			.catch((err) => {
				console.log(err)
			})
	}

	const handleTextFieldChange = (e) => {
		if (e.key === "Enter") {
			props.setSnippets([...props.snippets, textFieldValue])
			setTextFieldValue("")
			setShowNewSnippet(false)
		} else {
			setTextFieldValue(e.target.value)
		}
	}

	return (
		<div className="new-snippet-button-container">
			<button
				onClick={() => {
					setShowNewSnippet(true)
				}}
				className="new-snippet-button"
				hidden={showNewSnippet}
			>
				<i className="fa-solid fa-plus fa-lg plus-icon"></i>
				<span>&nbsp;</span>
				Take a snippet...
			</button>

			{showNewSnippet && (
				<div className="snippet">
					<form
						className="snippet-text-edit-container"
						onSubmit={handleTextFieldSubmit}
					>
						<TextareaAutosize
							className="snippet-text-edit-area"
							id="textfield-input"
							type="text"
							value={textFieldValue}
							onChange={handleTextFieldChange}
							autoFocus
							onFocus={function (e) {
								var val = e.target.value
								e.target.value = ""
								e.target.value = val
							}}
						/>
						<button
							className="snippet-text-edit-done-button"
							type="submit"
						>
							Done
						</button>
					</form>
				</div>
			)}
		</div>
	)
}

export default NewSnippetButton
