import "./style.css"
import { Fragment, useState } from "react"
import { Listbox, Transition } from "@headlessui/react"
import { ChevronDownIcon } from "@heroicons/react/24/solid"

function classNames(...classes) {
	return classes.filter(Boolean).join(" ")
}

function capitalizeFirstLetter(str) {
	return str.charAt(0).toUpperCase() + str.slice(1)
}

const ModeSelector = ({ selectedMode, setSelectedMode }) => {
	const options = ["chat", "freewrite"]

	return (
		<Listbox className='' value={selectedMode} onChange={setSelectedMode}>
			{({ open }) => (
				<>
					<div className='relative mt-4 w-3/4'>
						<Listbox.Button className='relative w-full cursor-default rounded-md border border-gray-300 mode-select text-white border-none font-normal align-middle p-3 focus:outline-none bg-delilahTertiary flex flex-row justify-center'>
							<span className='block truncate'>
								{capitalizeFirstLetter(selectedMode)}
							</span>
							<span className='pointer-events-none ml-1 mt-0.5'>
								<ChevronDownIcon
									className='h-5 w-5 text-white-600'
									aria-hidden='true'
								/>
							</span>
						</Listbox.Button>

						<Transition
							show={open}
							as={Fragment}
							leave='transition ease-in duration-100'
							leaveFrom='opacity-100'
							leaveTo='opacity-0'
						>
							<Listbox.Options className='absolute z-10 mt-1 max-h-60 w-full overflow-auto rounded-md bg-white py-1 text-base shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none sm:text-sm'>
								{options.map((option, index) => (
									<Listbox.Option
										key={option}
										className={({ active }) =>
											classNames(
												active ? "text-white bg-indigo-600" : "text-gray-900",
												"relative cursor-default select-none py-2 pl-3 pr-9"
											)
										}
										value={option}
										onClick={() => setSelectedMode(option)}
									>
										{({ selected, active }) => (
											<>
												<span
													className={classNames(
														selected ? "font-semibold" : "font-normal",
														"block truncate"
													)}
												>
													{capitalizeFirstLetter(option)}
												</span>

												{/* {selected ? (
													<span
														className={classNames(
															active ? "text-white" : "text-indigo-600",
															"absolute inset-y-0 right-0 flex items-center pr-4"
														)}
													>
														<CheckIcon className='h-5 w-5' aria-hidden='true' />
													</span>
												) : null} */}
											</>
										)}
									</Listbox.Option>
								))}
							</Listbox.Options>
						</Transition>
					</div>
				</>
			)}
		</Listbox>
	)
}

export default ModeSelector
