import "./new-thread-button.css"
import Request from "../Request/index.jsx"

const NewThreadButton = (props) => {
	const submitNewThread = () => {
		const threadRequest = new Request("threads/", {
			method: "POST",
			data: {
				title: "A new thread",
				description: "This is the new thread's description",
			},
		})
		threadRequest
			.then((res) => {
				props.setThreads([...props.threads, res.data])
				props.setSelectedThread(res.data.id)
			})
			.catch((err) => {
				console.log(err)
			})
	}

	return (
		<div className="new-thread-button" onClick={(e) => submitNewThread()}>
			<i className="fa-solid fa-plus fa-lg"></i>
			<span>&nbsp;</span>
			<p>New Thread</p>
		</div>
	)
}

export default NewThreadButton
